      } else {
        /**
         * Two different cases which need to deal with automatically adding add-ons:
         * 1. Desired state is stored in attributes already.
         *    One of the following must have happened previously:
         *          - The customer has clicked the toggle to enable the add-on, OR
         *          - We automatically added an add-on that was accepted by default
         *    In both cases we want to re-add the add-on. This case can occur if the
         *    cart was cleared and then the customer added a new item.
         * 2. Attribute does not exist. This means that the customer hasn't clicked the toggle
         *    so we should use the setting from the merchant in UpCart to decide if the add-on
         *    should be automatically added or not.
         */
        const shouldAddAutomatically =
          desiredAttrKey({ variantId }) in cart.attributes
            ? cart.attributes[desiredAttrKey({ variantId })] === DESIRED_ATTR_VALUE_ADDED
            : acceptedByDefault;
        if (shouldAddAutomatically) {
          // TODO: refactor this to make default acceptance more clear.
          //       It's not obvious that this hook will auto-add. However,
          //       it needs to be part of state initialization.
          handleEnable();
        } else {
          setAddonState({ type: AddonState.DISABLED });
        }
      }
    },
