const handleSelection = async ({
    selection,
    isVariant,
    parent,
  }: {
    selection: any;
    parent?: any;
    isVariant: boolean;
  }) => {
    if (isVariant) {
      const parentItem = selectedItems.find((item: GqlProduct) => item.id === parent.id);
      if (parentItem) {
        // If the parent exists, add/remove it from the variant array.
        let newItem = deepCopy(parentItem);

        newItem.variants.edges = [
          ...removeOrAddItem({ arr: newItem.variants.edges, item: selection, isVariant: true, isMulti: multiProduct }),
        ];
        if (newItem.variants.edges.length === 0) {
          // If we just deselected the last variant, we also want to deselect the parent
          setSelectedItems([...removeOrAddItem({ arr: selectedItems, item: parentItem, isMulti: multiProduct })]);
        } else {
          // In the normal case where we selected/deslected a single variant and we still
          // have at least one selected, we only want to change the selected state of
          // the variant, not the parent.

          const newArray = replaceItem(selectedItems, parentItem, newItem);
          setSelectedItems([...newArray]);
        }
      } else {
        // If the parent does not exist, add the parent and remove all other variants
        let parentCopy = deepCopy(parent);
        parentCopy.variants.edges = [{ node: selection }];
        setSelectedItems([...removeOrAddItem({ arr: selectedItems, item: parentCopy, isMulti: multiProduct })]);
      }
    } else {
      // Not a variant, selection on top-level item

      // Fetch all variants for the product
      let productWithVariants = await fetchAllVariants(selection.id);
      selection.variants = productWithVariants.variants;

      if (multiProduct) {
        //Select all variants
        setSelectedItems([...removeOrAddItem({ arr: selectedItems, item: selection, isMulti: multiProduct })]);
      } else {
        //Single product - select only the first variant
        let selectionCopy = deepCopy(selection);
        const firstVariant = selectionCopy.variants.edges?.[0];
        selectionCopy.variants.edges = firstVariant ? [firstVariant] : [];
        setSelectedItems([...removeOrAddItem({ arr: selectedItems, item: selectionCopy, isMulti: multiProduct })]);
      }
    }
  };

