const defaultSelectedProducts = useMemo(() => {
    let defaultSelectedProducts: any = [];

    let row = upsellItems.find((row) => row.id == currentId);
    if (row) {
      let productSelection: UpsellV2SelectedProducts | null;
      if (productType === ProductType.TRIGGER) {
        productSelection =
          row.trigger.on === 'specific' && row.trigger.selection.type === 'Product' ? row.trigger.selection : null;
      } else {
        productSelection = row.upsell.type === 'Product' ? row.upsell : null;
      }
      //The row should always exist
      if (productSelection) {
        productSelection.products.forEach((product) => {
          //Create the variant resource array
          let variants = product?.variants?.map((variantId) => {
            return {
              id: 'gid://shopify/ProductVariant/' + variantId,
            };
          });

          defaultSelectedProducts.push({
            ...product,
            variants: {
              edges: variants?.map((variant) => {
                return {
                  node: variant,
                };
              }),
            },
          });
        });
      }
    }
    return defaultSelectedProducts;
  }, [upsellItems, currentId, productType]);

