          // cause an invalid selector error when using document.querySelector if they weren't in quotes
          const itemQuerySelector = `[href*="${handle}"]`;
          // Wrap in try/catch in case the query selector is invalid for some reason. We still want to render
          // the upsell even if we can't find the element
          try {
            const handleALink = document.querySelector(itemQuerySelector);
            const el = handleALink?.closest('li.slide');
            platform.publicApi.upcartOnRenderUpsell(product, el);
          } catch (err) {
            console.log(`Unable to find product with querySelector("${itemQuerySelector}"):`, err);
            sentryCaptureException(err);
          }
          // ---- END window.upcartOnRenderUpsell

          for (let upsellItem of triggeredUpsells) {
            let upsell = upsellItem.upsell.products[0] || null;
            if (upsell?.handle === handle) {
              if (upsell.variants) {
                // when variants array is present, we trigger only on the variants specified
                const productWithOnlyTriggeredVariants = {
                  ...product,
                  variants: product.variants.filter((variant) => upsell.variants!.includes(String(variant.id))),
                };
                triggeredUpsellProductsWithThisHandle.push({
                  upsellId: upsellItem.id,
                  fetchedProduct: productWithOnlyTriggeredVariants,
                });
              } else {
                // when the variants array is missing, we trigger on any variant for the product
                triggeredUpsellProductsWithThisHandle.push({ upsellId: upsellItem.id, fetchedProduct: product });
              }
            }
          }

          // only include products that have at least one variant to show
          const productsWithVariants = triggeredUpsellProductsWithThisHandle.filter(
            ({ fetchedProduct }) => fetchedProduct.variants.length > 0,
          );

          return productsWithVariants;
