 7
        {% if line_item.selling_plan_allocation.selling_plan.id == selling_plan_allocation.selling_plan.id %}selected="selected"{% endif %}
 8
      >
 9
        {{ selling_plan_allocation.selling_plan.name }}
 10
      </option>
 11
    {% endfor %}
 12 </select>
