import { Shop, ShopModel } from '../modules/shops/shops.model';
import { RequestWithShop } from '../types/express.types';
import Shopify from '@shopify/shopify-api';

export const getShopDocFromRequest = async (req: RequestWithShop): Promise<Shop> => {
  if (req.shopDoc) {
    return req.shopDoc;
  }

  const shop = Shopify.Utils.sanitizeShop(req.query.shop as string);
  const shopDoc = await ShopModel.findOne({ domain: shop }).lean();
  req.shopDoc = shopDoc;
  return shopDoc;
};

