import CustomButton from '@components/common/CustomButton';
import ProductSelectModal from '@components/common/ProductSelectModal';
import ConfigureUpsellsModal from '@components/ConfigureUpsellsModal';
import { Checkbox, LegacyStack, Link } from '@shopify/polaris';
import { convertUpsellsToV2 } from '@storefront/src/modules/CartPopup/UpsellsModule/helper';
import { Field, useFormikContext } from 'formik';
import { random } from 'lodash';
import { useCallback, useEffect, useMemo, useRef, useState } from 'react';
import { Collection, Product } from 'server/modules/shopify/graphql/generated';
import {
  UpsellRecommendationIntent,
  UpsellV2Item,
  UpsellV2Product,
  UpsellV2ResourceType,
  UpsellV2SelectedCollection,
  UpsellV2SelectedProducts,
} from '../../../../../common/defaultMetafields';
import { CartEditorFormValues } from '../types';
import styles from './styles.module.scss';
import { useTranslation } from 'react-i18next';
import { SelectField } from '@components/common/FormField';

export enum ActionType {
  ADD = 'ADD',
  DELETE = 'DELETE',
  EDIT = 'EDIT',
  ALL = 'ALL', //Use all products
}

export enum ProductType {
  TRIGGER = 'trigger',
  UPSELL = 'upsell',
}

export type onActionParams = {
  productType?: ProductType;
  id?: string;
  action: ActionType;
};

/**
 * This component and its children have a lot of case handling around the type "UpsellItem"
 * After the multi-product updates we're handling the upsell and trigger as an array instead of an object.
 * Which makes the already loaded UpsellItem interface/type more complicated to deal with but this is necessary so that old upsells can be loaded and edited
 */
const SelectProducts = () => {
  const { values, setFieldValue, handleSubmit, isSubmitting } = useFormikContext<CartEditorFormValues>();
  const [productType, setProductType] = useState<ProductType>(null);
  const [currentId, setCurrentId] = useState(null);
  const addTriggerRef = useRef(null);
  const [activeModal, setActiveModal] = useState(false); //The main modal
  const [showResourcePickerModal, setShowResourcePickerModal] = useState(false); //Secondary resource picker modal
  const [upsellItems, setUpsellItems] = useState<Array<UpsellV2Item>>([]);
  const { t } = useTranslation();

  /**
   * Convert all upsell items to V2 format (if necessary)
   */
  useEffect(() => {
    const upsellItemsV2 = convertUpsellsToV2({ upsellItemsV1orV2: values.upcartEditorSettings.upsellsItems });
    setUpsellItems(upsellItemsV2);
  }, [values.upcartEditorSettings.upsellsItems]);

  //Handle actions from the list of upsells

(
    () => {
      const addonLineItem = cart.items.find(
        (item) => isBeamAddon(item, platform.settings) && item.variant_id === variantId,
      );
      if (addonLineItem) {
        setAddonState({ type: AddonState.ENABLED, lineItemKey: addonLineItem.key });
      } else {
        /**
         * Two different cases which need to deal with automatically adding add-ons:
         * 1. Desired state is stored in attributes already.
         *    One of the following must have happened previously:
         *          - The customer has clicked the toggle to enable the add-on, OR
         *          - We automatically added an add-on that was accepted by default
         *    In both cases we want to re-add the add-on. This case can occur if the
         *    cart was cleared and then the customer added a new item.
         * 2. Attribute does not exist. This means that the customer hasn't clicked the toggle
         *    so we should use the setting from the merchant in UpCart to decide if the add-on
         *    should be automatically added or not.
         */
        const shouldAddAutomatically =
          desiredAttrKey({ variantId }) in cart.attributes
            ? cart.attributes[desiredAttrKey({ variantId })] === DESIRED_ATTR_VALUE_ADDED
            : acceptedByDefault;
        if (shouldAddAutomatically) {
          // TODO: refactor this to make default acceptance more clear.
          //       It's not obvious that this hook will auto-add. However,
          //       it needs to be part of state initialization.
          handleEnable();
        } else {
          setAddonState({ type: AddonState.DISABLED });
        }
      }
    },
    { cart, hasFetchedCart },
  )

const addProductToCart = useCallback(
    (id: number | string, properties: ItemToAddToCart['properties']) => {
      platform.cart
        .addItemToCart(
          { variantId: Number(id), quantity: 1, properties: { ...properties, __upcartUpsell: id } },
          { opensCart: AddToCartOpensCart.NEVER },
        )
        .then(() => {
          platform.publicApi.upcartOnAddUpsell(String(id));
          refetchCart();
        })
        .catch((e) => console.error(e));
    },
    [refetchCart, platform],
  );

  const upsellsToShow = useMemo(() => {
    // Allow custom modification of upsells before display. This can include
    // changing properties of the upsell products, or removing/adding products.
    let finalUpsells = upsells;
    try {
      const modifiedUpsells = platform.publicApi.upcartModifyListOfUpsells(upsells);
      if (Array.isArray(modifiedUpsells)) {
        finalUpsells = modifiedUpsells;
      }
    } catch (e) {
      console.error(e);
      sentryCaptureException(e);
    }

    if (upsellsShouldLimit && Number(maximumUpsellsToShow) > 0) {
      finalUpsells = finalUpsells.splice(0, Number(maximumUpsellsToShow));
    }

    return finalUpsells;
  }, [platform, upsells, upsellsShouldLimit, maximumUpsellsToShow]);

  return (
    <div>
      {upsellsToShow.length !== 0 && (
        <PreviewCarousel
          platform={platform}
          items={upsellsToShow}
          onAddProduct={addProductToCart}
          cartIsLoading={cartIsLoading}
        />
      )}
    </div>
  );
}

