export function colorTextEmphasized(colorGroup?: ColorGroup, legacy?: boolean) {
    return colorGroup?.foreground
        ? colorGroup?.foreground?.adjust({
              l: (l) => (isLight(colorGroup?.foreground, legacy) ? Math.min(l + 15, 100) : l - 10),
          })
        : colorGroup?.background?.adjust({
              s: (s) => (s > 50 ? s : Math.min(s + 15, 100)),
              l: (l) => (isLight(colorGroup?.background, legacy) ? Math.max(l - 77.5, 0) : 98.3),
          });
}

export function colorTextSubdued(colorGroup?: ColorGroup, legacy?: boolean) {
    return colorGroup?.foreground
        ? colorGroup?.foreground?.adjust({
              l: (l) => (isLight(colorGroup?.foreground, legacy) ? Math.max(l - 35, 0) : l + 43.2),
          })
        : colorGroup?.background?.adjust({
              s: (s) => (s > 50 ? Math.max(s - 55, 0) : s),
              l: (l) => (isLight(colorGroup?.background, legacy) ? Math.max(l - 49.9, 10) : Math.min(l + 63.2, 90)),
          });
}

export function colorActionHovered(colorGroup?: ColorGroup) {
    return colorGroup?.background?.adjust({
        l: (l) => l - 10,
    });
}

export function colorActionPressed(colorGroup?: ColorGroup) {
    return colorGroup?.background?.adjust({
        l: (l) => l - 10,
    });
}

export function colorActionText(colorGroup?: ColorGroup, legacy?: boolean) {
    return (
        colorGroup?.foreground ??
        colorGroup?.background?.adjust({
            l: () => (isLight(colorGroup?.background, legacy) ? 4 : 96),
        })
    );
}

export function colorActionTextHovered(colorGroup?: ColorGroup) {
    return colorGroup?.background?.adjust({
        l: (l) => l - 10,
    });
}

export function colorActionTextPressed(colorGroup?: ColorGroup) {
    return colorGroup?.background?.adjust({
        l: (l) => l - 10,
    });
}

function colorsFromOverrides(overrideColors: Partial<RoleColorOverrides>) {
    return (Object.keys(overrideColors) as (keyof RoleColorOverrides)[]).reduce<Partial<RoleColors>>((colors, key) => {
        const colorOrGroup = overrideColors[key];

        if (colorOrGroup == null) return colors;

        const { background, foreground, accent } = colorOrGroup;

        return {
            ...colors,
            [key]: {
                background: background ? normalizeColor(background) : undefined,
                foreground: foreground ? normalizeColor(foreground) : undefined,
                accent: accent ? normalizeColor(accent) : undefined,
            },
        };
    }, {});
}

export function colorBorder(colorGroup?: ColorGroup, legacy?: boolean) {
    return colorGroup?.background?.adjust({
        s: (s) => (s > 50 ? Math.max(s - 15, 0) : s),
        l: (l) => (isLight(colorGroup?.background, legacy) ? Math.max(l - 8.8, 0) : Math.min(l + 11.3, 90)),
    });
}

