const SelectIconColor = ({ value, onChange }: Field<string>) => {
    const [active, setActive] = useState(false);
    const toggleActive = useCallback(() => setActive((active) => !active), []);
    const currentAppearance = iconAppearance.find(({ appearance }) => appearance === value)!;

    const activator = (
        <Button
            onClick={toggleActive}
            disclosure
            icon={
                <div
                    style={{
                        ...colorStyle,
                        backgroundColor: currentAppearance.color,
                        margin: '2px',
                    }}
                />
            }
        />
    );

    const iconsList = iconAppearance.map(({ appearance, color: backgroundColor }) => {
        return {
            value: appearance,
            label: '',
            media: (
                <div
                    style={{
                        ...colorStyle,
                        backgroundColor,
                    }}
                />
            ),
        };
    });

    return (
        <div>
            Select Icon Color
            <Popover active={active} activator={activator} autofocusTarget="first-node" onClose={toggleActive}>
                <OptionList title="" onChange={(value) => onChange(value[0])} options={iconsList} selected={[value]} />
            </Popover>
        </div>
    );
};

