import { default as ReactGlobal } from 'react';
import {
    NoteRemoveChange,
    NoteUpdateChange,
} from '@shopify/ui-extensions/build/ts/surfaces/checkout/api/checkout/checkout';
import {
    NoteWidgetConfig,
    Note as NoteTypeV1,
} from '../../../web/server/src/modules/checkout-widgets/checkout-widgets.model';
import { NotesContentType, NoteType as NoteTypeV2 } from '../../../web/server/src/modules/checkout-v2/checkout-v2.dtos';
import { parseTextToString } from '../utils/parseDifferentVersions';
import { CheckoutWidgetProps } from '../types';

type RenderNoteType = NoteWidgetConfig['notes'][0] & {
    name: string;
    isSelected: boolean;
    textBoxValue: string;
    fromInitialCart: false;
};

const stringifyNote = (notes: [string, string][]): string => {
    return notes.map(([key, value]) => (value ? `${key} : ${value}` : key)).join(' \n ');
};

const parseNote = (notes: string, keys: string[]): { [key: string]: string } => {
    if (!notes) return {};

    const noteArray = notes.split(' \n ');
    const noteEntries = noteArray.map((note) => {
        const [key, value] = note.split(' : ');
        return keys.includes(key || '') ? [key, value || ''] : [note, ''];
    });

    return Object.fromEntries(noteEntries);
};

type NoteWidgetType = NoteWidgetConfig | NotesContentType;
type NoteType = NoteTypeV1 | NoteTypeV2;

type CurrentNote = {
    fromInitialCart: boolean;
    name: string;
    textBoxValue: string;
    isSelected: boolean;
} & Partial<NoteType>;

export const NoteWidget: ReactGlobal.FC<CheckoutWidgetProps<NoteWidgetType>> = ({ globals }) => {
    const {
        React,
        components: { BlockStack, Checkbox, TextField },
        hooks: { useNote, useApplyNoteChange },
        widget: { notes: notesFromDb = [] },
    } = globals;

    const checkedNotesFromDb = React.useMemo(
        () =>
            (notesFromDb as NoteType[])
                .filter((note) => Boolean(note.checked))
                .map(({ text, ...note }) => ({ ...note, text: parseTextToString(text) })),
        [notesFromDb]
    );
    const checkedNotesText = React.useMemo(() => checkedNotesFromDb.map(({ text }) => text), [checkedNotesFromDb]);

    const [loading, setLoading] = React.useState(false);
    const [error, setError] = React.useState('');

    const notesFromCart = useNote() || '';
    const applyNoteChange = useApplyNoteChange();

    const parsedNotesFromCart = React.useMemo(() => {
        let preparedParsedNotes: { [name: string]: string } = parseNote(notesFromCart, checkedNotesText);

        return preparedParsedNotes;
    }, [notesFromCart, checkedNotesText]);

import { Button, HorizontalStack, InlineError, Text, VerticalStack } from '@shopify/polaris';
import { notEmpty, useDynamicList, useField, useForm } from '@shopify/react-form';
import React, { useCallback, useEffect, useMemo, useState } from 'react';
import useCheckoutFormState from '../../../../../hooks/useCheckoutFormState';
import { NoteWidgetConfig, WidgetSettingsProps } from '../../../../../types';
import Triggers from '../triggers';
import { Note } from './note';

export type NoteType = NoteWidgetConfig['notes'][0];
const defaultNoteValues = {
    checked: false,
    text: '',
    showTextBox: false,
    textBoxPlaceholder: 'Write your note here!',
};

const emptyNoteFactory = (): NoteType => ({
    ...defaultNoteValues,
    id: Date.now(),
});

export const NoteSettings = ({
    widgetData,
    handleConfigChange,
    handleChange,
    hasChanges,
}: WidgetSettingsProps<NoteWidgetConfig>) => {
    const initialWidgetValues = useMemo(() => widgetData, []);

    const {
        fields: notesList,
        dirty,
        addItem,
        removeItem,
        value,
    } = useDynamicList<NoteType>(
        {
            list: initialWidgetValues.notes.map((note) => ({
                ...defaultNoteValues,
                ...note,
            })),
            validates: {
                text: [notEmpty('Note text is required')],
            },
        },
        emptyNoteFactory
    );

    const {
        fields: { notes, triggers },
        dirty: formDirty,
    } = useForm({
        fields: {
            notes: notesList,
            triggers: useField(initialWidgetValues.triggers),
        },
    });

    const commonDirty = useMemo(() => hasChanges || dirty || formDirty, [hasChanges, dirty, formDirty]);
    const [noteLengthError, setNoteLengthError] = useState(false);
    const notesErrors = notesList.reduce((errors, note) => {
        return [
            ...errors,
            ...Object.values(note)
                .map((field) => field?.error)
                .filter(Boolean),
        ] as string[];
    }, [] as string[]);

    const [commonErrors, setCommonErrors] = useState(!!triggers.error || !!notesErrors.length);

    useCheckoutFormState(handleChange, commonDirty, commonErrors);

    //handle change nested config options in redux
    const handleNotesChange = useCallback(handleConfigChange('notes'), [handleConfigChange]);

