    const previousList = usePrevious<DynamicList<NoteType>['value']>(list.value);

    const handleListItemChange = () => {
        content.onChange({
            ...content.value,
            notes: list.value,
        });
    };

    const onSave = () => {
        handleListItemChange();
    };

    useEffect(() => {
        if (!previousList) return; //initial render
        if (list.value.length === previousList?.length) {
            const changedPriority = previousList.some((value, index) => value.id !== list.value?.[index]?.id);
