                                    <Button onClick=

"Write your note here"
                                        label=""
                                        value={text.value.text}
                                        onChange={(val) => text.onChange({ ...text.value, text: val })}
                                        autoComplete="off"
                                    />
                                </div>
                            </HorizontalStack>
                            <VerticalStack gap="2">
                                <Checkbox label="Show text box when selected" {...asChoiceField(showTextBox)} />
                                {!!showTextBox.value && (
                                    <TextField
                                        label="Define text box placeholder"
                                        {...textBoxPlaceholder}
                                        autoComplete="off"
                                    />
                                )}
                            </VerticalStack>
                        </VerticalStack>
                    );
                })()}
            </Modal>
            <PriorityModal
                open={priorityModal}
                onClose={() => {
                    list.reset();
                    setPriorityModal(false);
                }}
                dynamicList={list}
                onSave={() => {
                    onSave();
                    setPriorityModal(false);
                }}
                Item={({ item, index, ...props }: LineItemWrapperProps & { item: NoteType; index: number }) => {
