import { Button, Checkbox, Text, TextField, VerticalStack, HorizontalStack } from '@shopify/polaris';
import { asChoiceField, FieldDictionary } from '@shopify/react-form';
import React, { FC, useEffect } from 'react';
import type { NoteType } from './note-settings';
import styles from './styles.module.scss';

interface NoteSettingsProps {
    fields: FieldDictionary<NoteType>;
    index: number;
    removeBadge(index: number): void;
}

export const Note: FC<NoteSettingsProps> = ({ fields, removeBadge, index }) => {
    const { text, checked, showTextBox, textBoxPlaceholder } = fields;

    useEffect(() => {
        text.runValidation();
    }, []);

    return (
        <VerticalStack gap="2">
            <HorizontalStack blockAlign="center" align="space-between">
                <Text variant="headingXs" as="h3">{`Note ${index + 1}`}</Text>
                <Button plain destructive onClick={() => removeBadge(index)}>
                    Remove note
                </Button>
            </HorizontalStack>
            <HorizontalStack gap="1" blockAlign="start">
                <Checkbox label="" {...asChoiceField(checked)} />
                <div className={styles.flexStretch}>
                    <TextField placeholder="Write your note here" label="" {...text} autoComplete="off" />
                </div>
            </HorizontalStack>
            <VerticalStack gap="2">
                <Checkbox label="Show text box when selected" {...asChoiceField(showTextBox)} />
                {!!showTextBox.value && (
                    <TextField label="Define text box placeholder" {...textBoxPlaceholder} autoComplete="off" />
                )}
            </VerticalStack>
        </VerticalStack>
    );
};

