static async createAfterSellPlan({
        sanitizedNewAfterSellPlan,
    }: {
        sanitizedNewAfterSellPlan: AfterSellPlan;
    }): Promise<Result> {
        const validationResult = BillingValidationService.validateAfterSellPlanLogic({
            sanitizedAfterSellPlan: sanitizedNewAfterSellPlan,
        });
        if (!validationResult.isValid) {
            return {
                success: false,
                reason: validationResult.reason,
            };
        }
        const createPlanResult = await AppendOnlyAfterSellPlansService.createPlan(sanitizedNewAfterSellPlan);
        return createPlanResult;
    }

    static async getPossibleFeaturesForUser({
        shopifyService,
    }: {
        shopifyService: ShopifyService;
    }): Promise<AfterSellPaidFeature[]> {
        const possibleFeatures: AfterSellPaidFeature[] = [AfterSellPaidFeature.POST_PURCHASE];
        const hasCheckoutExtensibilityStoreAccess = await shopifyService.getHasCheckoutExtensibilityAccess();
        if (hasCheckoutExtensibilityStoreAccess) {
            possibleFeatures.push(AfterSellPaidFeature.CHECKOUT);
        }
        return possibleFeatures;
    }

    private static convertRestRecurringChargeToShopifyCharge(
        recurringChargeRest: ActiveRecurringChargeRest
    ): ShopifyChargeToValidate {
        return {
            chargeName: recurringChargeRest.name,
            recurringChargeConfigOnChargeAccept: {
                monthlyAmount: Number(recurringChargeRest.price),
            },
            maxMonthlyUsageAmount: recurringChargeRest.capped_amount ? Number(recurringChargeRest.capped_amount) : 0, // default to 0 if capped_amount is undefined
        };
    }
}

