    const user = await User.findOne({ myshopify_domain: shopifyService.shop });
    const shouldUseImprovedInventory =
        user.featureFlags?.improvedInventoryIsEnabled && user.additionalOauthScopes?.includes('read_inventory');
    if (!shouldUseImprovedInventory) {
        return;
    }

    const locationsQuery = await shopifyService.graphql.GetLocations();
    const allowedLocationIds = locationsQuery.locations.nodes
        // Exclude brick & mortar (physical) locations.
        // See Linear issue BEAM-1508
        .filter((loc) => loc.fulfillsOnlineOrders)
        // Exclude locations that are specifically excluded by the merchant in settings.
        // See Linear issue BEAM-1509
        .filter((loc) => !(settings.excludedLocationIds || []).includes(loc.legacyResourceId))
        .map((loc) => loc.legacyResourceId);
    // This could be optimized by skipping checking inventory levels for items that do not track inventory,
    // are already out of stock, or allow selling when out of stock. These changes would reduce the number
    // of API calls required, but they add some complexity to the logic below so they are not implemented for now.
    const inventoryItemIds = mut_products
        .map((product) => product.variants.map((variant) => variant.inventory_item_id))
        .flat();
    // NOTE: this may end up making a LOT of API calls for offers with many products and many variants. This may cause
    //       us to hit rate limits.
    // NOTE: this API has pagination, but we do not use it because pagination forces sequential requests (need to wait
    //       to get next link) which can cause very high latency (multiple seconds). Instead we chunk data such that
    //       we keep the total result set size under 250. We would need to chunk requests anyways to avoid the 50 inventory
    //       item id limit, so this solution is actually easier to implement since we can ignore pagination.
    const MAX_QUERY_FILTER_SIZE = 50;
    const MAX_PAGE_SIZE = 250;
    assert(
        allowedLocationIds.length <= MAX_QUERY_FILTER_SIZE,
        `[${shopifyService.shop}] Too many locations. Expected store to have not more than ${MAX_QUERY_FILTER_SIZE} locations.`
    );
    const chunkedInventoryItemIds =
        allowedLocationIds.length === 0
            ? [] // prevent divide by 0 error
            : chunkArray(
                  inventoryItemIds,
                  // create largest chunks possible, without exceeding either the max page size or the max query filter size
                  Math.min(Math.floor(MAX_PAGE_SIZE / allowedLocationIds.length), MAX_QUERY_FILTER_SIZE)
              );
    const inventoryLevelPromises = [];

const shouldUseImprovedInventory =
