            (change) => change.type === 'add_variant' || change.type === 'add_subscription'
        );
        assert(
            addChanges.length === 1,
            `[${shop}] Changeset rejected because it does not contain exactly one add_variant or add_subscription change. Checkout token: "${referenceId}".`
        );
        const variantId = addChanges[0].variantId;
        const { variant } = await XShopify.Rest.send(
            XShopify.Rest.prepare(XShopify.Rest.endpoints['GET /admin/api/2023-04/variants/{variant_id}.json'], {
                params: {
                    creds: { shop: shopifyService.shop, accessToken: shopifyService.accessToken },
                    path: { variant_id: variantId },
                },
                callerLocation: 'PostPurchase::SignChangeset::CheckInventory',
            })
        );
        const singleVariantProductArr = [{ variants: [variant] }];
        await mut_correctInventoryCounts({ shopifyService, settings, mut_products: singleVariantProductArr });
        const variantIsInStock = getVariantIsInStock(singleVariantProductArr[0].variants[0]);
        if (!variantIsInStock) {
            throw new Error(
                `[${shop}] Changeset rejected because variant is out of stock at allowed locations. Variant ID: "${variant.id}". Checkout token: "${referenceId}".`
            );
        }
    }

    res.json({ token, shouldReplaceUpsell });
}

async function incrementCheckoutRevisionNumber({ shop, accessToken }) {
    const { revisionNumber } = await CheckoutRevisionNumberModel.findOneAndUpdate(
        { shop },
        { shop, $inc: { revisionNumber: 1 } },
        { upsert: true, new: true }
    );
    const appDiscountsQuery = await fetch(`https://${shop}/admin/api/2023-04/graphql.json`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-Shopify-Access-Token': accessToken,
        },
        body: JSON.stringify({
            query: `{
                automaticDiscountNodes(first: 25, query: "status:active AND type:app") {
                    nodes {
                        automaticDiscount {
                            __typename
                            ... on DiscountAutomaticApp {
                                discountId
                            }
                        }
                    }
                }
            }`,
        }),
    }).then((res) => res.json());
    const ownerIds = appDiscountsQuery.data?.automaticDiscountNodes.nodes
        .filter((node) => node.automaticDiscount.__typename === 'DiscountAutomaticApp')
        .map((node) => node.automaticDiscount.discountId);
    await fetch(`https://${shop}/admin/api/2023-04/graphql.json`, {
        method: 'POST',
        headers: {
            'Content-Type': 'application/json',
            'X-Shopify-Access-Token': accessToken,
        },
        body: JSON.stringify({
            query: `mutation MetafieldsSet($metafields: [MetafieldsSetInput!]!){
                metafieldsSet(metafields: $metafields) {
                    metafields {
                        id
                    }
                }
            }`,
            variables: {
                metafields: ownerIds.map((ownerId) => ({
                    ownerId,
                    namespace: '$app:upsell-offer-discount-function',
                    key: 'private-checkout-revision-number',
                    value: String(revisionNumber),
                    type: 'integer',
                })),
            },
        }),
    }).then((res) => res.json());
}

