const getPageIdentifier = (shopifyObj) => {
        const sessionName = 'aftersell-external-partner-last-order-id';
        const shopifyOrderId = shopifyObj.checkout.order_id;
        const checkoutStep = shopifyObj.Checkout.step;
        const checkoutUpdatedAt = new Date(shopifyObj.checkout.updated_at);
        try {
            if (checkoutStep === 'thank_you') {
                window.localStorage.setItem(sessionName, shopifyOrderId);
                return orderConfirmationPageIdentifier;
            }
            const lastOrderId = window.localStorage.getItem(sessionName);
            if (lastOrderId && lastOrderId === String(shopifyOrderId)) {
                return orderStatusPageIdentifier;
            } else {
                window.localStorage.setItem(sessionName, shopifyOrderId);
            }
        } catch (err) {
            // ignore if there is issue with accessing localStorage
        }
        const currentTime = new Date();
        const checkoutUpdatedAtPlusOneMinute = new Date(checkoutUpdatedAt.getTime() + 1000 * 120);
        return checkoutUpdatedAtPlusOneMinute > currentTime
            ? orderConfirmationPageIdentifier
            : orderStatusPageIdentifier;
    };

    async function main() {
        injectPlaceholders();
        if (window.Shopify && window.Shopify.checkout && window.Shopify.Checkout) {
            const shopifyObj = window.Shopify;
            const attributes = await getAttributes(shopifyObj);
            const identifier = getPageIdentifier(shopifyObj);
            initRokt(accountId, attributes, identifier);
        }
    }
    main();
}

{
    const discountCodes = useDiscountCodes();
    const cartLines = useCartLines();
    const storage = useStorage();

    const _applyCartLinesChange = useApplyCartLinesChange();
    const safeApplyCartLinesChange = useCallback(
        async (change: CartLineChange) => {
            const error = await storage.read(STORAGE_ERROR_KEY);
            if (error) {
                // This is required to prevent repeatedly re-adding an out of stock item. When an out of stock item is
                // added to checkout, the customer gets a popup saying that the item is out of stock the next time they
                // change anything or try to complete checkout. If we keep trying to add an out of stock item, the customer
                // will never be able to complete the checkout.
                //
                // There may be other errors that could block checkout that we haven't found, in those cases we should also
                // stop trying whatever we're doing.
                //
                // TODO: we should inform the customer when errors occur (e.g., banner saying gift out of stock)
                console.error('AfterSell GWP: skipping change due to previous error', { error, change });
                return;
            }
            const result = await _applyCartLinesChange(change);
            if (result.type === 'error') {
                console.error('AfterSell GWP: change failed', { change, result });
                await storage.write(STORAGE_ERROR_KEY, result.message);
            }
        },
        [_applyCartLinesChange, storage]
    );
    const registerCorrectState = useCallback(async () => {
        const error = await storage.read(STORAGE_ERROR_KEY);
        if (error) {
            // We clear the error when we reach a correct state so that we can try to apply changes again
            // later if needed. This is most useful for merchant testing so that they don't need to clear
            // local storage if an error happens.
            //
            // There are many errors that happen due to race conditions with Shopify's checkout extension
            // APIs or incorrect data provided to our extension (e.g., empty line items). If one of these
            // incorrect errors happens, we don't want to completely shut off GWP functionality. We can't
            // be certain whether an error was real or not.
            console.info('AfterSell GWP: clearing error because correct state was reached', { error });
            await storage.delete(STORAGE_ERROR_KEY);
        }
    }, [storage]);

    const discountCodeIsApplied = useMemo(
        () => discountCodes.some((discount) => settings.discountCodes.includes(discount.code)),
        [discountCodes, settings]
    );

import { ShopifyService } from '../shopify/shopify.service';
import UserModel from '../users/users.model';
import { calculateShopMetrics } from '../billing/usage-charging/shop-metrics';
import { getGlobalFeatureFlagEnabled } from '../global-feature-flags/global-feature-flags';

type AfterSellBrandingProps = { shop: string; shopifyService?: ShopifyService };

const getShopifyService = async ({ shop, shopifyService }: AfterSellBrandingProps): Promise<ShopifyService> => {
    if (shopifyService) {
        return shopifyService;
    }

    const user = await UserModel.findOne({ myshopify_domain: shop }, { accessToken: 1 }).lean();
    if (!user) throw new Error(`User not found for shop ${shop}`);
    if (!user.accessToken) throw new Error(`No access token found for shop ${shop}`);

    return new ShopifyService({ shop, accessToken: user.accessToken });
};

export const isEligibleForAftersellBranding = async ({
    shop,
    shopifyService: _shopifyService,
}: AfterSellBrandingProps): Promise<boolean> => {
    const shopifyService = await getShopifyService({ shop, shopifyService: _shopifyService });
    const user = await UserModel.findOne({ myshopify_domain: shop }, { plan_name: 1 }).lean();

    if (!user) throw new Error(`User not found for shop ${shop}`);

    // CASE 1: Only eligible if on basic shopify plan;
    if (user.plan_name !== 'basic') return false;

    // CASE 2: Only eligible if on lowest pricing tier (i.e. less than 100 orders in the past 30 days)
    const now = new Date();
    const thirtyDaysAgo = new Date(now.getTime() - 30 * 24 * 60 * 60 * 1000);
    const shopMetrics = await calculateShopMetrics({ shopifyService, period: { start: thirtyDaysAgo, end: now } });
    if (shopMetrics.totalOrders >= 100) return false;

    return true;
};

export const maybeEnableAftersellBranding = async ({
    shop,
    shopifyService: _shopifyService,
    isNewUser,
}: AfterSellBrandingProps & { isNewUser: boolean }): Promise<void> => {
    // Enables Aftersell branding on the Post-Purchase page if the merchant is eligible for it.

    // this is called within installing, and a try catch is used to ensure it never interferes with that
    try {
        // CASE 1: Only automatically enable if the user is new
        if (!isNewUser) return;

        const postPurchaseAftersellBrandingEnabled = await getGlobalFeatureFlagEnabled('postPurchaseBrandingIsEnabled');
        if (!postPurchaseAftersellBrandingEnabled) return;

        const shopifyService = await getShopifyService({ shop, shopifyService: _shopifyService });

        // CASE 2: Only automatically enable if the user is eligible for it
        const eligible = await isEligibleForAftersellBranding({ shop, shopifyService });
        if (eligible) {
            await UserModel.findOneAndUpdate({ myshopify_domain: shop }, { postPurchaseAftersellBranding: true });
        }
    } catch (err) {
        console.error(`[${shop}] Error enabling Aftersell branding: ${err}`);
    }
};

