            )
        );
        inventoryLevelPromises.push(p);
    }
    const inventoryLevelsResponses = await Promise.all(inventoryLevelPromises);
    const inventoryLevels = inventoryLevelsResponses.map((response) => response.inventory_levels).flat();

    for (const mut_product of mut_products) {
        for (const mut_variant of mut_product.variants) {
            const levels = inventoryLevels.filter((level) => level.inventory_item_id === mut_variant.inventory_item_id);
            // There may be no levels tracked for this item if all locations in which it is stocked are excluded.
            if (levels.length === 0) {
                // mut
                mut_variant.inventory_quantity = 0;
            } else {
                const tracksInventory = levels.every((level) => level.available !== null);
                if (tracksInventory) {
                    const countAtAllowedLocations = levels.reduce((acc, curr) => acc + curr.available, 0);

                    // mut
                    mut_variant.inventory_quantity = countAtAllowedLocations;
                }
            }
        }
    }
}

static async getUserNeedsToAcceptNewAfterSellPlan({
        shopifyService,
    }: {
        shopifyService: ShopifyService;
    }): Promise<NeedsNewCharge | DoesNotNeedNewCharge> {
        const acceptedAfterSellPlanResult = await this.getUserAcceptedAndActiveAfterSellPlan({ shopifyService });
        if (!acceptedAfterSellPlanResult.success) {
            return {
                needsToAcceptNewPlan: true,
                reason: acceptedAfterSellPlanResult.reason,
            };
        }
        if (acceptedAfterSellPlanResult.data.aftersellPlan.isDeprecated_forceUsersToAcceptNewPlanOnNextLogin) {
            return {
                needsToAcceptNewPlan: true,
                reason: `User is currently on a deprecated AfterSell plan. Current AfterSell plan: "${acceptedAfterSellPlanResult.data.aftersellPlan.id}".`,
            };
        }
        const pendingPlanChange = await PendingPlanChangesService.getUserPendingPlanChange({
            myshopifyDomain: shopifyService.shop,
        });
        if (pendingPlanChange) {
            return {
                needsToAcceptNewPlan: true,
                reason: `User has a pending AfterSell plan change. Pending AfterSell plan change: "${pendingPlanChange.pendingAfterSellPlanId}".`,
            };
        }
        return {
            needsToAcceptNewPlan: false,
        };
    }

    static getUserAccessiblePaidFeatures({
        acceptedAfterSellPlan,
    }: {
        acceptedAfterSellPlan: AfterSellPlan;
    }): AfterSellPaidFeature[] {
        const paidFeatures: AfterSellPaidFeature[] = [];
        for (const feature of acceptedAfterSellPlan.shopifyCharge.recurringChargeConfigOnChargeAccept.paysForFeatures) {
            paidFeatures.push(feature);
        }
        for (const usageCharge of acceptedAfterSellPlan.monthlyUsageCharges) {
            paidFeatures.push(usageCharge.paysForFeature);
        }
        return paidFeatures;
    }

