export const shopRedact = async (req, res) => {
    res.sendStatus(200);

    const shop = req.get('x-shopify-shop-domain');

    await Promise.allSettled([
        /**
         * Merchant personal data
         */
        // Online sessions may include names/emails of employees in `onlineAccessInfo`
        SessionModel.deleteMany({ shop, isOnline: true }),
        User.updateOne(
            { myshopify_domain: shop },
            {
                // Specific location data could be identifiable, so we will remove it to be safe.
                // We do not need to remove province and country since these could not identify any individual.
                // Note that this is supposed to be the business address, but the merchant could have entered
                // their home address here so we will remove it just in case.
                address1: null,
                address2: null,
                zip: null,
                city: null,
                latitude: null,
                longitude: null,

                // Note that these are supposed to be the business contacts, but they could still be personally
                // identificable based on the format (e.g., john.doe@company.com) so we will remove them to be safe.
                customer_email: null,
                email: null,
                importantAccountEmail: null,
                adsNetworkEmail: null,
                phone: null,

                shop_owner: null,
            }
        ),

        /**
         * Customer personal data
         */
        // NOTE: this should match the list in the "customers/redact" webhook
        EventsModel.deleteMany({ shop }), // customerId
        JourneyLinkerModel.deleteMany({ shop }), // checkoutToken
        PossibleHoldsModel.deleteMany({ shop }), // checkoutToken
        ReplacementUpsellAttemptModel.deleteMany({ shop }), // checkoutToken
