        StorefrontSessionModel.deleteMany({ shop }), // cartToken, checkoutToken, and customerTriggerData
        TyImpressionDedupeModel.deleteMany({ shop }), // checkoutToken
        VisibilityEventModel.deleteMany({ myshopifyDomain: shop }), // checkoutToken and payload in some cases
        VisitedFunnelsModel.deleteMany({ shop }), // checkoutToken and customerId
    ]);
};

