const getPageIdentifier = (shopifyObj) => {
        const sessionName = 'aftersell-external-partner-last-order-id';
        const shopifyOrderId = shopifyObj.checkout.order_id;
        const checkoutStep = shopifyObj.Checkout.step;
        const checkoutUpdatedAt = new Date(shopifyObj.checkout.updated_at);
        try {
            if (checkoutStep === 'thank_you') {
                window.localStorage.setItem(sessionName, shopifyOrderId);
                return orderConfirmationPageIdentifier;
            }
            const lastOrderId = window.localStorage.getItem(sessionName);
            if (lastOrderId && lastOrderId === String(shopifyOrderId)) {
                return orderStatusPageIdentifier;
            } else {
                window.localStorage.setItem(sessionName, shopifyOrderId);
            }
        } catch (err) {
            // ignore if there is issue with accessing localStorage
        }
        const currentTime = new Date();
        const checkoutUpdatedAtPlusOneMinute = new Date(checkoutUpdatedAt.getTime() + 1000 * 120);
        return checkoutUpdatedAtPlusOneMinute > currentTime
            ? orderConfirmationPageIdentifier
            : orderStatusPageIdentifier;
    };

