        injectPlaceholders();
        if (window.Shopify && window.Shopify.checkout && window.Shopify.Checkout) {
            const shopifyObj = window.Shopify;
            const attributes = await getAttributes(shopifyObj);
            const identifier = getPageIdentifier(shopifyObj);
            initRokt(accountId, attributes, identifier);
        }
    }
