    const _applyCartLinesChange = useApplyCartLinesChange();
    const safeApplyCartLinesChange = useCallback(
        async (change: CartLineChange) => {
            const error = await storage.read(STORAGE_ERROR_KEY);
            if (error) {
                // This is required to prevent repeatedly re-adding an out of stock item. When an out of stock item is
                // added to checkout, the customer gets a popup saying that the item is out of stock the next time they
                // change anything or try to complete checkout. If we keep trying to add an out of stock item, the customer
                // will never be able to complete the checkout.
                //
                // There may be other errors that could block checkout that we haven't found, in those cases we should also
                // stop trying whatever we're doing.
                //
                // TODO: we should inform the customer when errors occur (e.g., banner saying gift out of stock)
                console.error('AfterSell GWP: skipping change due to previous error', { error, change });
                return;
            }
            const result = await _applyCartLinesChange(change);
            if (result.type === 'error') {
                console.error('AfterSell GWP: change failed', { change, result });
                await storage.write(STORAGE_ERROR_KEY, result.message);
            }
        },
        [_applyCartLinesChange, storage]
    );
    const registerCorrectState = useCallback(async () => {
        const error = await storage.read(STORAGE_ERROR_KEY);
        if (error) {
            // We clear the error when we reach a correct state so that we can try to apply changes again
            // later if needed. This is most useful for merchant testing so that they don't need to clear
            // local storage if an error happens.
            //
            // There are many errors that happen due to race conditions with Shopify's checkout extension
            // APIs or incorrect data provided to our extension (e.g., empty line items). If one of these
            // incorrect errors happens, we don't want to completely shut off GWP functionality. We can't
            // be certain whether an error was real or not.
            console.info('AfterSell GWP: clearing error because correct state was reached', { error });
