        const activationDeepLink = new URL(`https://${this.config.shop}/admin/themes/${mainThemeId}/editor`);
        activationDeepLink.searchParams.append('context', 'apps');
        activationDeepLink.searchParams.append('activateAppId', `${themeAppExtensionUuid}/${appEmbedFileName}`);

        return { isEnabled: appEmbedIsEnabled, activationDeepLink: activationDeepLink.toString() };
    }

    async getActiveRecurringCharge(): Promise<ActiveRecurringChargeRest | null> {
        const shopify = new Shopify.Clients.Rest(this.config.shop, this.config.accessToken);
        const recurringChargesResponse = await shopify.get({
            path: `recurring_application_charges`,
            query: { fields: 'id,name,status,price,capped_amount' },
        });
        const recurringCharges = (recurringChargesResponse.body as any)
            .recurring_application_charges as RecurringCharge[];
        const activeRecurringCharge = recurringCharges.find((charge) => charge.status === 'active');
        return activeRecurringCharge ?? null;
    }

    /**
     * Gets the metafield that is added to orders when a post-purchase upsell is accepted through AfterSell.
     * If there is no metafield, returns null.
     *
     * This can be used to check if an order has a post-purchase upsell from AfterSell. If the metafield is non-null then the order
     * has a post-purchase upsell from AfterSell.
     */

for (const chunkOfInventoryItemIds of chunkedInventoryItemIds) {
        const p = XShopify.Rest.send(
            XShopify.Rest.prepare(
                XShopify.Rest.endpoints[
                    'GET /admin/api/2023-04/inventory_levels.json?location_ids&inventory_item_ids&limit'
                ],
                {
                    params: {
                        creds: { shop: shopifyService.shop, accessToken: shopifyService.accessToken },
                        query: {
                            location_ids: allowedLocationIds.join(','),
                            inventory_item_ids: chunkOfInventoryItemIds.join(','),
                            limit: MAX_PAGE_SIZE,
                        },
                    },
                    callerLocation: 'PostPurchase::CorrectInventoryCounts',
                }
            )
        );
        inventoryLevelPromises.push(p);
    }
    const inventoryLevelsResponses = await Promise.all(inventoryLevelPromises);
    const inventoryLevels = inventoryLevelsResponses.map((response) => response.inventory_levels).flat();

    for (const mut_product of mut_products) {
        for (const mut_variant of mut_product.variants) {
            const levels = inventoryLevels.filter((level) => level.inventory_item_id === mut_variant.inventory_item_id);
            // There may be no levels tracked for this item if all locations in which it is stocked are excluded.
            if (levels.length === 0) {
                // mut
                mut_variant.inventory_quantity = 0;
            } else {
                const tracksInventory = levels.every((level) => level.available !== null);
                if (tracksInventory) {
                    const countAtAllowedLocations = levels.reduce((acc, curr) => acc + curr.available, 0);

                    // mut
                    mut_variant.inventory_quantity = countAtAllowedLocations;
                }
            }
        }
    }
}

static async createAfterSellPlan({
        sanitizedNewAfterSellPlan,
    }: {
        sanitizedNewAfterSellPlan: AfterSellPlan;
    }): Promise<Result> {
        const validationResult = BillingValidationService.validateAfterSellPlanLogic({
            sanitizedAfterSellPlan: sanitizedNewAfterSellPlan,
        });
        if (!validationResult.isValid) {
            return {
                success: false,
                reason: validationResult.reason,
            };
        }
        const createPlanResult = await AppendOnlyAfterSellPlansService.createPlan(sanitizedNewAfterSellPlan);
        return createPlanResult;
    }

    static async getPossibleFeaturesForUser({
        shopifyService,
    }: {
        shopifyService: ShopifyService;
    }): Promise<AfterSellPaidFeature[]> {
        const possibleFeatures: AfterSellPaidFeature[] = [AfterSellPaidFeature.POST_PURCHASE];
        const hasCheckoutExtensibilityStoreAccess = await shopifyService.getHasCheckoutExtensibilityAccess();
        if (hasCheckoutExtensibilityStoreAccess) {
            possibleFeatures.push(AfterSellPaidFeature.CHECKOUT);
        }
        return possibleFeatures;
    }

    private static convertRestRecurringChargeToShopifyCharge(
        recurringChargeRest: ActiveRecurringChargeRest
    ): ShopifyChargeToValidate {
        return {
            chargeName: recurringChargeRest.name,
            recurringChargeConfigOnChargeAccept: {
                monthlyAmount: Number(recurringChargeRest.price),
            },
            maxMonthlyUsageAmount: recurringChargeRest.capped_amount ? Number(recurringChargeRest.capped_amount) : 0, // default to 0 if capped_amount is undefined
        };
    }
}

export const shopRedact = async (req, res) => {
    res.sendStatus(200);

    const shop = req.get('x-shopify-shop-domain');

    await Promise.allSettled([
        /**
         * Merchant personal data
         */
        // Online sessions may include names/emails of employees in `onlineAccessInfo`
        SessionModel.deleteMany({ shop, isOnline: true }),
        User.updateOne(
            { myshopify_domain: shop },
            {
                // Specific location data could be identifiable, so we will remove it to be safe.
                // We do not need to remove province and country since these could not identify any individual.
                // Note that this is supposed to be the business address, but the merchant could have entered
                // their home address here so we will remove it just in case.
                address1: null,
                address2: null,
                zip: null,
                city: null,
                latitude: null,
                longitude: null,

                // Note that these are supposed to be the business contacts, but they could still be personally
                // identificable based on the format (e.g., john.doe@company.com) so we will remove them to be safe.
                customer_email: null,
                email: null,
                importantAccountEmail: null,
                adsNetworkEmail: null,
                phone: null,

                shop_owner: null,
            }
        ),

        /**
         * Customer personal data
         */
        // NOTE: this should match the list in the "customers/redact" webhook
        EventsModel.deleteMany({ shop }), // customerId
        JourneyLinkerModel.deleteMany({ shop }), // checkoutToken
        PossibleHoldsModel.deleteMany({ shop }), // checkoutToken
